class HourPolygon{constructor(o,t,s,i){this.options={restitution:1,friction:0},this.hourBodies=Bodies.polygon(o,t,s,i,this.options),Matter.Composite.add(world,this.hourBodies),this.s=s}show(){let o=this.hourBodies.position,t=this.hourBodies.angle;push(),translate(o.x,o.y),rotate(t),stroke(255),fill(0),strokeWeight(1),polygon(0,0,this.s,random(5,10)),pop()}remove(){Matter.Composite.remove(world,this.hourBodies)}}